<template>
	<view bubble="true" class="myp-icon-box" :style="boxStyle" :hover-class="'myp-hover-'+hover"
		@tap="toClickIcon">
		<image v-if="isImageSrc" class="myp-image" :src="name" mode="aspectFill" :style="iconStyle"></image>
		<text v-else :class="[fontClass||'myp-iconfont', 'myp-color-' +type, 'myp-size-'+size]"
			:style="iconStyle">{{fontClass?name:icons[name]}}</text>
		<slot></slot>
	</view>
</template>

<script>
	// #ifdef APP-NVUE
	const dom = uni.requireNativePlugin('dom');
	dom.addRule('fontFace', {
		'fontFamily': "gxicon",
		'src': "url('data:font/ttf;charset=utf-8;base64,')"
	})
	// #endif

	import icons from './icons.js'

	import {
		isSrc
	} from '@/mypUI/utils/utils.js'
	export default {
		props: {
			stop: {
				type: Boolean,
				default: true
			},
			/**
			 * 自定义的字体样式类
			 */
			fontClass: {
				type: String,
				default: ''
			},
			/**
			 * 字体图标的名字，允许是图片路径
			 */
			name: {
				type: String,
				default: 'right'
			},
			/**
			 * 图标的颜色主题
			 */
			type: {
				type: String,
				default: ''
			},
			/**
			 * 图标的尺寸主题
			 */
			size: {
				type: String,
				default: 'l'
			},
			/**
			 * hover主题
			 */
			hover: {
				type: String,
				default: 'none'
			},
			/**
			 * 图标或者图片样式
			 */
			iconStyle: {
				type: String,
				default: ""
			},
			/**
			 * 外层样式
			 */
			boxStyle: {
				type: String,
				default: ''
			}
		},
		data() {
			return {
				icons: icons
			}
		},
		computed: {
			isImageSrc() {
				const isUrlSrc = isSrc(this.name)
				if (isUrlSrc) {
					return true
				}
				const last_len = this.name.lastIndexOf(".");
				if (last_len > 0) {
					const len = this.name.length;
					const ext = this.name.substring(last_len, len)
					return ['.png', '.jpg', '.jpeg', '.webp', '.gif', '.bmp'].includes(ext.toLowerCase())
				}
				return false
			},
			isColor() {
				return (this.type || "").indexOf("#") === 0;
			}
		},
		methods: {
			toClickIcon(e) {
				this.$emit("iconClicked")
				this.stop && e.stopPropagation && e.stopPropagation()
			}
		}
	}
</script>

<style lang="scss" scoped>
	.myp-iconfont {
		font-family: gxicon;
		text-decoration: none;
		text-align: center;
	}

	.myp-icon-box {
		position: relative;
		/* #ifndef APP-NVUE */
		display: flex;
		box-sizing: border-box;
		/* #endif */
		justify-content: center;
		align-items: center;
	}

	.myp-image {
		width: 32rpx;
		height: 32rpx;
	}
</style>
